import { useState, useEffect } from 'react';
import React from 'react';
import List from './List';
import QRScanner from './QRScanner';

//declare something here to pass into QRScanner as a prop, so 
//other components can access the data


const App = () => {

    const [modalActivate, setModalActivate] = useState(false);
    // const examineData = (id) => {
    //     orders.forEach((order) => {
    //         if (order.id == id) {
    //             console.log("Yah");
    //             return true;
    //         }
    //     });
    // }

    const examineData = (id) => {
        orders.forEach((order) => {
            // i COULD have some specific state here that gets passed into
            // the QRScanner, or...
            if (order.id == id) {
                setModalActivate(true);
            }
        })
    }

    const [orders, setOrders] = useState([])

    return (
    <div>
        <h1 className="" style={{padding: '20px', paddingTop:'40px', textAlign:'center', boxShadow:"0px 10px 0px 0px black blur"}}>BuddieMeal QR Code Scanner</h1>
        <QRScanner examineData={examineData} modalActivate={modalActivate} setModalActivate={setModalActivate}/>
        <List orders={orders}/>
    </div>
        );
}

export default App;
